import * as github from "@blink-sdk/github";
import { convertToModelMessages, streamText } from "ai";
import * as blink from "blink";

const githubAppID = process.env.GITHUB_APP_ID;
const githubAppClientID = process.env.GITHUB_APP_CLIENT_ID;
const githubAppClientSecret = process.env.GITHUB_APP_CLIENT_SECRET;
const githubAppPrivateKey = process.env.GITHUB_APP_PRIVATE_KEY;
const githubWebhookSecret = process.env.GITHUB_WEBHOOK_SECRET;
if (
  !githubAppID ||
  !githubAppClientID ||
  !githubAppClientSecret ||
  !githubAppPrivateKey ||
  !githubWebhookSecret
) {
  throw new Error(
    "GITHUB_APP_ID, GITHUB_APP_CLIENT_ID, GITHUB_APP_CLIENT_SECRET, GITHUB_APP_PRIVATE_KEY, and GITHUB_WEBHOOK_SECRET must be set"
  );
}

const agent = blink.agent();

agent.on("chat", async ({ messages, context }) => {
  return streamText({
    model: blink.model("anthropic/claude-sonnet-4.5"),
    system: `You are a code review agent for GitHub.

Your responsibilities:
1. Review pull requests when they are opened or updated with new commits
2. Create and update check runs called "Code Review" to report your findings
3. Provide constructive, actionable feedback on code quality, potential bugs, and best practices
4. Respond to review comments and requests for changes
5. Monitor check runs and help fix issues when they fail

When reviewing code:
- Start by creating a check run in "in_progress" status
- Review the changes thoroughly using the available tools
- Look for bugs, security issues, code quality problems, and violations of best practices
- Provide specific, actionable feedback with line annotations when possible
- Update the check run with "success" if everything looks good, or "failure" if issues are found
- Be constructive and helpful in your feedback

When you receive notifications about new commits (synchronize events), perform a fresh review of the updated changes.
`,
    messages: convertToModelMessages(messages),
    tools: blink.tools.withContext(
      {
        get_pull_request: github.tools.get_pull_request,
        list_pull_request_files: github.tools.list_pull_request_files,
        get_pull_request_diff: github.tools.get_pull_request_diff,
        repository_read_file: github.tools.repository_read_file,
        list_pull_request_reviews: github.tools.list_pull_request_reviews,
        list_pull_request_review_comments:
          github.tools.list_pull_request_review_comments,
        create_check_run: github.tools.create_check_run,
        update_check_run: github.tools.update_check_run,
        list_check_runs_for_ref: github.tools.list_check_runs_for_ref,
      },
      {
        appAuth: {
          appId: githubAppID,
          privateKey: atob(githubAppPrivateKey),
          cache: context.store,
        },
      }
    ),
  });
});

agent.on("request", async (request, context) => {
  const url = new URL(request.url);
  if (url.pathname.startsWith("/github")) {
    const { Webhooks } = await import("@octokit/webhooks");
    const webhooks = new Webhooks({
      secret: githubWebhookSecret,
    });
    const [id, event, signature] = [
      request.headers.get("x-github-delivery"),
      request.headers.get("x-github-event"),
      request.headers.get("x-hub-signature-256"),
    ];
    if (!signature || !id || !event) {
      return new Response("Unauthorized", { status: 401 });
    }

    const ensureChatAndMessage = async (props: {
      prNodeID: string;
      prNumber: number;
      prTitle: string;
      repository: string;
      userMessage: string;
      modelMessage: string;
    }) => {
      const chatKey = `chat-id-for-pr-${props.prNodeID}`;
      let chat = await context.store.get(chatKey);

      // If no chat exists, create one now
      if (!chat) {
        const newChat = await context.chat.ensure(chatKey);
        chat = newChat.key;
        await context.store.set(chatKey, chat);

        // Also store by repo/pr_number for check_run webhooks
        const altChatKey = `chat-id-for-pr-${props.repository}/${props.prNumber}`;
        await context.store.set(altChatKey, chat);
      }

      await context.chat.message(
        chat,
        {
          role: "user",
          parts: [
            {
              type: "text",
              text: props.userMessage,
            },
            {
              type: "text",
              text: props.modelMessage,
            },
          ],
        },
        {
          behavior: "enqueue",
        }
      );
    };

    webhooks.on("pull_request", async (event) => {
      const pr = event.payload.pull_request;
      const action = event.payload.action;

      // Store chat ID using node_id for consistency across webhook types
      const chatKey = `chat-id-for-pr-${pr.node_id}`;

      // Handle PR opened or reopened - create a chat and initiate review
      if (action === "opened" || action === "reopened") {
        const chat = await context.chat.ensure(chatKey);

        // Store the chat ID for future webhook events (using multiple keys for different webhook types)
        await context.store.set(chatKey, chat.key);
        // Also store by repo/pr_number for check_run webhooks which don't include node_id
        const altChatKey = `chat-id-for-pr-${event.payload.repository.owner.login}/${event.payload.repository.name}/${pr.number}`;
        await context.store.set(altChatKey, chat.key);

        // Send initial message to start the review process
        await context.chat.message(chat.key, {
          role: "user",
          parts: [
            {
              type: "text",
              text: `A pull request was ${action}.`,
            },
            {
              type: "text",
              text: `A pull request was ${action} and needs review.

Repository: ${event.payload.repository.owner.login}/${event.payload.repository.name}
Pull Request: #${pr.number} - ${pr.title}
Author: ${pr.user?.login}
Head SHA: ${pr.head.sha}
Head Ref: ${pr.head.ref}
Base Ref: ${pr.base.ref}

Description:
${pr.body ?? "No description provided."}

---

Please perform a code review:
1. Create a check run called "Code Review" in "in_progress" status
2. Review the changes in this pull request
3. Update the check run with your findings

Be thorough but constructive in your review.`,
            },
          ],
        });
      }

      // Handle PR synchronize - new commit pushed
      if (action === "synchronize") {
        await ensureChatAndMessage({
          prNodeID: pr.node_id,
          prNumber: pr.number,
          prTitle: pr.title,
          repository: `${event.payload.repository.owner.login}/${event.payload.repository.name}`,
          userMessage: `New commits were pushed to the pull request.`,
          modelMessage: `A webhook was received for new commits on the pull request.

Repository: ${event.payload.repository.owner.login}/${event.payload.repository.name}
Pull Request: #${pr.number} - ${pr.title}
Author: ${pr.user?.login}
New Head SHA: ${pr.head.sha}
Head Ref: ${pr.head.ref}
Base Ref: ${pr.base.ref}

Description:
${pr.body ?? "No description provided."}

---

Please review the changes:
1. Create a check run called "Code Review" in "in_progress" status (if one doesn't exist, or update existing)
2. Review the changes in this pull request
3. Update the check run with your findings

Be thorough but constructive in your review.`,
        });
      }

      // Handle PR merged
      if (action === "closed" && pr.merged) {
        await ensureChatAndMessage({
          prNodeID: pr.node_id,
          prNumber: pr.number,
          prTitle: pr.title,
          repository: `${event.payload.repository.owner.login}/${event.payload.repository.name}`,
          userMessage: `The pull request was merged.`,
          modelMessage: `A webhook was received for a pull request merge.

Pull request ID: ${pr.id}
Pull request state: ${pr.state}
Pull request merged: ${pr.merged}
Pull request merged at: ${pr.merged_at}
`,
        });
      }

      // Handle PR closed without merging
      if (action === "closed" && !pr.merged) {
        await ensureChatAndMessage({
          prNodeID: pr.node_id,
          prNumber: pr.number,
          prTitle: pr.title,
          repository: `${event.payload.repository.owner.login}/${event.payload.repository.name}`,
          userMessage: `The pull request was closed without merging.`,
          modelMessage: `A webhook was received for a pull request closure.

Pull request ID: ${pr.id}
Pull request state: ${pr.state}
Pull request closed at: ${pr.closed_at}
`,
        });
      }
    });

    webhooks.on("pull_request_review", async (event) => {
      if (event.payload.sender.login === process.env.GITHUB_BOT_LOGIN) {
        return;
      }
      await ensureChatAndMessage({
        prNodeID: event.payload.pull_request.node_id,
        prNumber: event.payload.pull_request.number,
        prTitle: event.payload.pull_request.title,
        repository: `${event.payload.repository.owner.login}/${event.payload.repository.name}`,
        userMessage: `A pull request was reviewed (${event.payload.review.state}) by ${event.payload.sender.login}.`,
        modelMessage: `A webhook was received for a pull request review.

Repository: ${event.payload.repository.owner.login}/${event.payload.repository.name}
Pull Request: #${event.payload.pull_request.number} - ${event.payload.pull_request.title}
Review ID: ${event.payload.review.id}
Review state: ${event.payload.review.state}
Reviewer: ${event.payload.sender.login}
Review commit: ${event.payload.review.commit_id}

Review body:
${event.payload.review.body ?? "No body provided."}

---

There may be comments on the review you should read using the list_pull_request_review_comments tool. If the review requests changes, you should analyze the feedback and consider if any action is needed.
`,
      });
    });

    webhooks.on("pull_request_review_comment", async (event) => {
      if (event.payload.sender.login === process.env.GITHUB_BOT_LOGIN) {
        return;
      }

      const association = event.payload.comment.author_association;
      if (
        association !== "COLLABORATOR" &&
        association !== "MEMBER" &&
        association !== "OWNER"
      ) {
        return;
      }

      await ensureChatAndMessage({
        prNodeID: event.payload.pull_request.node_id,
        prNumber: event.payload.pull_request.number,
        prTitle: event.payload.pull_request.title,
        repository: `${event.payload.repository.owner.login}/${event.payload.repository.name}`,
        userMessage: `A pull request review comment was ${event.payload.action} by ${event.payload.sender.login}.`,
        modelMessage: `A webhook was received for a pull request review comment.

Repository: ${event.payload.repository.owner.login}/${event.payload.repository.name}
Pull Request: #${event.payload.pull_request.number} - ${event.payload.pull_request.title}
Comment ID: ${event.payload.comment.id}
Commenter: ${event.payload.sender.login}
Comment commit: ${event.payload.comment.commit_id}
File: ${event.payload.comment.path}
${event.payload.comment.line ? `Line: ${event.payload.comment.line}` : ""}

Comment body:
${event.payload.comment.body}

---

This is a code review comment on specific lines of code. Analyze the feedback and consider if any response or action is needed.
`,
      });
    });

    webhooks.on("issue_comment", async (event) => {
      if (event.payload.sender.login === process.env.GITHUB_BOT_LOGIN) {
        return;
      }

      const association = event.payload.comment.author_association;
      if (
        association !== "COLLABORATOR" &&
        association !== "MEMBER" &&
        association !== "OWNER"
      ) {
        return;
      }

      await ensureChatAndMessage({
        // The "id" is not consistent between issue_comment and pull_request webhooks.
        // The "node_id" is.
        // Try getting `/repos/coder/coder/issues/<pr>` and `/repos/coder/coder/pulls/<pr>`,
        // the `id` property will be different.
        prNodeID: event.payload.issue.node_id,
        prNumber: event.payload.issue.number,
        prTitle: event.payload.issue.title,
        repository: `${event.payload.repository.owner.login}/${event.payload.repository.name}`,
        userMessage: `A comment was ${event.payload.action} on the pull request by ${event.payload.sender.login}.`,
        modelMessage: `A webhook was received for a pull request comment (issue_comment event).

Repository: ${event.payload.repository.owner.login}/${event.payload.repository.name}
Pull Request: #${event.payload.issue.number} - ${event.payload.issue.title}
Comment ID: ${event.payload.comment.id}
Commenter: ${event.payload.sender.login}

Comment body:
${event.payload.comment.body}

---

This is a general comment on the pull request. Read the comment and determine if any response or action is needed.
`,
      });
    });

    // This is when a thread is resolved. I don't think we need to do anything here.
    webhooks.on("pull_request_review_thread", async (event) => {
      //
    });

    webhooks.on("check_run.completed", async (event) => {
      if (
        event.payload.check_run.conclusion === "success" ||
        event.payload.check_run.conclusion === "skipped"
      ) {
        // Just ignore - we don't care about successful check runs.
        return;
      }
      for (const pr of event.payload.check_run.pull_requests) {
        // This is an old check run.
        if (event.payload.check_run.head_sha !== pr.head.sha) {
          continue;
        }

        // Look up or create chat using repo/pr_number since check_run payloads don't include node_id
        const altChatKey = `chat-id-for-pr-${event.payload.repository.owner.login}/${event.payload.repository.name}/${pr.number}`;
        let chat = await context.store.get(altChatKey);

        // If no chat exists, create one now
        if (!chat) {
          const newChat = await context.chat.ensure(altChatKey);
          chat = newChat.key;
          await context.store.set(altChatKey, chat);
        }

        await context.chat.message(
          chat,
          {
            role: "user",
            parts: [
              {
                type: "text",
                text: `A check run was completed for a pull request.`,
              },
              {
                type: "text",
                text: `A webhook was received for a check run.

Check run ID: ${event.payload.check_run.id}
Check run name: ${event.payload.check_run.name}
Check run status: ${event.payload.check_run.status}
Check run conclusion: ${event.payload.check_run.conclusion}

---

If the check run fails, you are responsible for fixing the issue.
`,
              },
            ],
          },
          {
            behavior: "enqueue",
          }
        );
      }
    });

    // These are GitHub webhook requests.
    return webhooks
      .verifyAndReceive({
        id,
        name: event,
        payload: await request.text(),
        signature,
      })
      .then(() => {
        return new Response("OK", { status: 200 });
      })
      .catch((err) => {
        console.error("GitHub webhook error", err);
        return new Response("Error", { status: 500 });
      });
  }

  return new Response("Hey there!", { status: 200 });
});

agent.serve();

